import requests
from loading_bar import ProgressBar

class GoogleSheetEditor:

    def __init__(self):
        self.current_record = 1

    def delete_rows(self, google_sheet, google_authorisation):
        try:
            delete_data = self.get_rows(google_sheet, google_authorisation)['sheet1'][-1]['id']
            for record in reversed(range(1, delete_data)):
                    delete_row = requests.delete(f'{google_sheet}/{record+1}', headers=google_authorisation)
            self.delete_rows(google_sheet, google_authorisation)
        except IndexError:
            pass
        except KeyError:
            delete_data = self.get_rows(google_sheet, google_authorisation)
            print(delete_data)


    def get_rows(self, google_sheet, google_authorisation):
        get_data = requests.get(f'{google_sheet}', headers=google_authorisation)
        return get_data.json()

    def add_data(self,  product_data, google_sheet, google_authorisation):
        loading_bar = ProgressBar(product_data)
        for product in product_data:
            sheety_params = {
                'sheet1':
                    {
                        'id': product['sku'],
                        'title': product['product_name'],
                        'description': product['description'],
                        'link': product['url'],
                        'condition': 'new',
                        'price': product['price'],
                        'sale price': product['sale_price'],
                        'sale price effective date': product['sale_price_date'],
                        'availability': product['availability'],
                        'imageLink': product['product_image'],
                        'gtin': product['gtin'],
                        'mpn': product['mpn'],
                        'brand': product['brand'].upper(),
                        'googleProductCategory': product['product_category'],
                        'shipping': product['shipping'],
                        'productHighlight': product['product_highlight']
                    }
            }
            post_data = requests.post(f'{google_sheet}', json=sheety_params, headers=google_authorisation)
            if 200 <= post_data.status_code < 300:
                yield loading_bar.print_progress_bar(sheety_params['sheet1']['title'], ' uploaded to Google Feed')
                # print(loading_bar.print_progress_bar(sheety_params['sheet1']['title'], ' uploaded to Google Feed'))
            else:
                yield f'product not uploaded to Google Feed. Error code: {str(post_data.status_code)}'
                # print(f'product not uploaded to Google Feed. Error code: {str(post_data.status_code)}')
            self.current_record += 1



    def put_data(self,  product_data, google_sheet, google_authorisation):
        data = self.get_rows(google_sheet=google_sheet, google_authorisation=google_authorisation)
        print(data['sheet1'][2])



